// Full React component for a clickable animated gift box with fireworks and "Click Me!"
import React, { useEffect, useRef, useState } from 'react';

const GiftBox = () => {
    const canvasRef = useRef(null);
    const [step, setStep] = useState(1);

    useEffect(() => {
        const box = document.querySelector('.giftbox');
        const merrywrap = document.querySelector('.merrywrap');

        const handleClick = () => {
            if (step === 1) box.removeEventListener('click', handleClick);
            setStep(prev => {
                const next = prev + 1;
                merrywrap.className = `merrywrap step-${next}`;
                if (next === 4) reveal();
                else setTimeout(handleClick, [2000, 2000, 1000, 1000][prev - 1]);
                return next;
            });
        };

        const reveal = () => {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            let cw = window.innerWidth;
            let ch = window.innerHeight;
            canvas.width = cw;
            canvas.height = ch;

            let fireworks = [], particles = [], hue = 120, limiterTotal = 5,
                limiterTick = 0, timerTotal = 80, timerTick = 0, mousedown = false, mx, my;

            const random = (min, max) => Math.random() * (max - min) + min;
            const calculateDistance = (p1x, p1y, p2x, p2y) => {
                const x = p1x - p2x, y = p1y - p2y;
                return Math.sqrt(x * x + y * y);
            };

            class Firework {
                constructor(sx, sy, tx, ty) {
                    this.x = sx; this.y = sy; this.sx = sx; this.sy = sy; this.tx = tx; this.ty = ty;
                    this.distanceToTarget = calculateDistance(sx, sy, tx, ty);
                    this.distanceTraveled = 0;
                    this.coordinates = Array(3).fill([this.x, this.y]);
                    this.angle = Math.atan2(ty - sy, tx - sx);
                    this.speed = 2; this.acceleration = 1.05;
                    this.brightness = random(50, 70); this.targetRadius = 1;
                }
                update(i) {
                    this.coordinates.pop();
                    this.coordinates.unshift([this.x, this.y]);
                    this.targetRadius = this.targetRadius < 8 ? this.targetRadius + 0.3 : 1;
                    this.speed *= this.acceleration;
                    const vx = Math.cos(this.angle) * this.speed;
                    const vy = Math.sin(this.angle) * this.speed;
                    this.distanceTraveled = calculateDistance(this.sx, this.sy, this.x + vx, this.y + vy);
                    if (this.distanceTraveled >= this.distanceToTarget) {
                        createParticles(this.tx, this.ty);
                        fireworks.splice(i, 1);
                    } else {
                        this.x += vx;
                        this.y += vy;
                    }
                }
                draw() {
                    ctx.beginPath();
                    ctx.moveTo(...this.coordinates[this.coordinates.length - 1]);
                    ctx.lineTo(this.x, this.y);
                    ctx.strokeStyle = `hsl(${hue}, 100%, ${this.brightness}%)`;
                    ctx.stroke();
                    ctx.beginPath();
                    ctx.arc(this.tx, this.ty, this.targetRadius, 0, Math.PI * 2);
                    ctx.stroke();
                }
            }

            class Particle {
                constructor(x, y) {
                    this.x = x; this.y = y;
                    this.coordinates = Array(5).fill([this.x, this.y]);
                    this.angle = random(0, Math.PI * 2);
                    this.speed = random(1, 10);
                    this.friction = 0.95;
                    this.gravity = 1;
                    this.hue = random(hue - 20, hue + 20);
                    this.brightness = random(50, 80);
                    this.alpha = 1;
                    this.decay = random(0.015, 0.03);
                }
                update(i) {
                    this.coordinates.pop();
                    this.coordinates.unshift([this.x, this.y]);
                    this.speed *= this.friction;
                    this.x += Math.cos(this.angle) * this.speed;
                    this.y += Math.sin(this.angle) * this.speed + this.gravity;
                    this.alpha -= this.decay;
                    if (this.alpha <= this.decay) particles.splice(i, 1);
                }
                draw() {
                    ctx.beginPath();
                    ctx.moveTo(...this.coordinates[this.coordinates.length - 1]);
                    ctx.lineTo(this.x, this.y);
                    ctx.strokeStyle = `hsla(${this.hue}, 100%, ${this.brightness}%, ${this.alpha})`;
                    ctx.stroke();
                }
            }

            const createParticles = (x, y) => {
                for (let i = 0; i < 30; i++) particles.push(new Particle(x, y));
            };

            const loop = () => {
                requestAnimationFrame(loop);
                hue += 0.5;
                ctx.globalCompositeOperation = 'destination-out';
                ctx.fillStyle = 'rgba(0,0,0,0.5)';
                ctx.fillRect(0, 0, cw, ch);
                ctx.globalCompositeOperation = 'lighter';
                fireworks.forEach((f, i) => (f.draw(), f.update(i)));
                particles.forEach((p, i) => (p.draw(), p.update(i)));
                if (timerTick++ >= timerTotal) {
                    if (!mousedown) {
                        fireworks.push(new Firework(cw / 2, ch, random(0, cw), random(0, ch / 2)));
                        timerTick = 0;
                    }
                }
                if (limiterTick++ >= limiterTotal) {
                    if (mousedown) {
                        fireworks.push(new Firework(cw / 2, ch, mx, my));
                        limiterTick = 0;
                    }
                }
            };

            merrywrap.style.backgroundColor = 'transparent';
            loop();
        };

        document.querySelector('.giftbox')?.addEventListener('click', handleClick);
    }, [step]);

    return (
        <>
            <canvas id="canvas" ref={canvasRef}></canvas>
            <div id="merrywrap" className={`merrywrap step-${step}`}>
                <div className="giftbox">
                    <div className="cover">
                        <div></div>
                    </div>
                    <div className="box"></div>
                </div>
            </div>
        </>
    );
};

export default GiftBox;
